userDefinedLinkHintCss:
/* Link hint boxes */
div > .vimiumHintMarker {
  background: #232a2e;
  font-family: 'PragmataPro Mono', monospace;
  font-size: 10px;
  padding: 2px;
  border: 0.0em solid #a7c080;
  border-radius: 0.34em;
  box-shadow: 0em 0.1em 0.6em 0.1em rgba(0, 0, 0, 0.4);
  opacity: 0.6;
}

/* Link hint text */
div > .vimiumHintMarker span {
  color: #d3c6aa;
  font-size: inherit;
  text-shadow: none;
}

/* Link hint matching characters */
div > .vimiumHintMarker > .matchingCharacter {
  color: #6272a4;
}

/* HUD ("heads-up display") bar */
div.vimiumHUD {
  background: #232a2e;
  border: 1px solid #a7c080;
}

div.vimiumHUD .vimiumHUDSearchArea {
  background: #282a36;
}

div.vimiumHUD .hud-find {
  background: #232a3e;
  border: none;
  color: #a7c080;
}

div.vimiumHUD span#hud-find-input{
  color: #d3c6aa;  /* 'Foreground' color */
}

div.vimiumHUD span#hud-match-count {
  color: #a7c080;  /* 'Comment' color */
}

div.vimiumHUD .vimiumHUDSearchAreaInner {
  color: #d3c6aa;  /* 'Comment' color */
}

#vomnibar {
  font-family: 'PragmataPro Mono', monospace;
  background-color: #232a2e;
}

#vomnibar input {
  color: #d3c6aa;
  font: -moz-window;
  font-family: 'PragmataPro Mono', monospace;
  font-size: 20px;
  height: 34px;
  margin-bottom: 0;
  padding: 4px;
  background-color: #232a2e;
  border-radius: 3px;
  border: 1px solid #a7c080;
  box-shadow: #bd93f9 0px 0px 1px;
  width: 100%;
  outline: none;
  box-sizing: border-box;
}

#vomnibar .vomnibarSearchArea {
  display: block;
  padding: 10px;
  background-color: #232a2e;
  border-radius: 4px 4px 0 0;
  border-bottom: 0px solid #bd93f9;
}

#vomnibar ul {
  background-color: #2d353b;
  border-radius: 0 0 4px 4px;
  list-style: none;
  padding-top: 0;
  padding-bottom: 0;
  margin-block-start: 2px;
  margin-block-end: 0px;
}

#vomnibar li {
  border-bottom: 1px solid #7fbbb3;
  line-height: 1.1em;
  padding: 7px 10px;
  font-size: 16px;
  color: #7fbbb3;
  position: relative;
  display: list-item;
  margin: auto;
}

#vomnibar li .vomnibarBottomHalf {
  font-size: 15px;
  margin-top: 3px;
  padding: 2px 0;
}

#vomnibar li .vomnibarSource {
  color: #7fbbb3;
  margin-right: 4px;
}
#vomnibar li .vomnibarRelevancy {
  position: absolute;
  right: 0;
  top: 0;
  padding: 5px;
  background-color: #232a2e;
  color: #d3c6aa;
  font-family: monospace;
  width: 100px;
  overflow: hidden;
}

#vomnibar li .vomnibarUrl {
  white-space: nowrap;
  color: #a7c080;
}

#vomnibar li .vomnibarMatch {
  font-weight: bold;
  color: #e67e80;
}

#vomnibar li em, #vomnibar li .vomnibarTitle {
  color: #d3c6aa;
  margin-left: 4px;
  font-weight: normal;
}
#vomnibar li em { font-style: italic; }
#vomnibar li em .vomnibarMatch, #vomnibar li .vomnibarTitle .vomnibarMatch {
  color: #e67e80;
}

#vomnibar li.vomnibarSelected {
  background-color: #38484d;
  font-weight: normal;
}

#vomnibarInput::selection {
  background-color: #56635f;
}
